import React, { useEffect, useState } from "react";
import { ClimbingBoxLoader } from "react-spinners";

const ShowTable = () => {
  const [userData, setuserData] = useState([]);
  const deletItem = (id) => {
    alert(id);
    const data = localStorage.getItem("register");
    const dt = data.filter((ele, i) => {
      if (i !== id) {
        return ele;
      }
    });
    localStorage.setItem(dt)
    setuserData(dt);
  };

  useEffect(() => {
    const lenData = JSON.parse(localStorage.getItem("register"));
    console.log(lenData);
    setuserData(lenData);
  }, []);
  return (
    <>
      <div className="tableDiv">
        <h1>ShowTbal</h1>
        <table className="table table-dark">
          <thead>
            <tr>
              <th scope="col">ID</th>
              <th scope="col">NAME</th>
              <th scope="col">EMAIL</th>
              <th scope="col">AGE</th>
              <th scope="col">MOBILE</th>
              <th scope="col">GENDER</th>
              <th scope="col">CITY</th>
              <th colSpan={2} style={{ textAlign: "center" }}>
                ACTION
              </th>
            </tr>
          </thead>
          <tbody>
            {userData && userData ? (
              userData.map((ele, i) => {
                return (
                  <>
                    <tr>
                      <td>{ele.id}</td>
                      <td>{ele.fullName}</td>
                      <td>{ele.email}</td>
                      <td>{ele.age}</td>
                      <td>{ele.mobile}</td>
                      <td>{ele.gender}</td>
                      <td>{ele.city}</td>
                      <td>
                        <button
                          onClick={() => deletItem(i)}
                          className="btn btn-danger"
                        >
                          DELETE
                        </button>
                      </td>
                      <td>
                        <button className="btn btn-success">EDIT</button>
                      </td>
                    </tr>
                  </>
                );
              })
            ) : (
              <ClimbingBoxLoader
                cssOverride={{ textAlign: "center", justifyContent: "center" }}
              />
            )}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default ShowTable;
