import React from "react";
import { useForm } from "react-hook-form";

const RegLog = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <>
      <div className="container">
        <div></div>
        <h1>RGISTER FORM</h1>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div class="form-group">
            <p>
              Enter Your Fullname :
              <input
                class="form-control"
                {...register("fullName", { required: true, maxLength: 20 })}
              />
              {errors.fullName && (
                <span className="sp">Please Enter valid name </span>
              )}
            </p>
          </div>
          <div class="form-group">
            <p>
              Enter Email :
              <input
                class="form-control"
                {...register("email", {
                  required: true,
                  pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                })}
              />
              {errors.email && <span className="sp">@ is essential</span>}
            </p>
          </div>
          <div class="form-group">
            <p>
              Enter Your Age :
              <input
                class="form-control"
                type="number"
                {...register("age", { required: true, min: 18, max: 23 })}
              />
              {errors.age && <span className="sp">age is in 18-23</span>}
            </p>
          </div>

          <div class="form-group">
            <p>
              Enter YourMobile No. :
              <input
                class="form-control"
                {...register("mobile", {
                  required: true,
                  maxLength: 10,
                  minLength: 10,
                })}
              />
              {errors.mobile && (
                <span className="sp">Mobile No is Not valid </span>
              )}
            </p>
          </div>
          <div class="form-group">
            <p>
              <select {...register("category")}>
                <option value="">Select...</option>
                <option value="male">MALE </option>
                <option value="female">FEMALE</option>
                <option value="other">OTHER</option>
              </select>
            </p>
          </div>

          <input type="submit" />
        </form>
      </div>
    </>
  );
};

export default RegLog;
