import React from "react";
import { useForm } from "react-hook-form";

const RegLog = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <>
      <div className="container">
        <h1>RGISTER FORM</h1>
        <form onSubmit={handleSubmit(onSubmit)}>
          <p>
            Enter Your Fullname :
            <input
              {...register("fullName", { required: true, maxLength: 20 })}
            />
            {errors.fullName && (
              <span className="sp">Please Enter valid name </span>
            )}
          </p>

          <p>
            Enter Email :
            <input
              {...register("email", {
                required: true,
                pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
              })}
            />
            {errors.email && <span className="sp">@ is essential</span>}
          </p>
          <p>
            Enter Your Age :
            <input
              type="number"
              {...register("age", { required: true, min: 18, max: 23 })}
            />
            {errors.age && <span className="sp">age is in 18-23</span>}
          </p>

          <p>
            Enter YourMobile No. :
            <input
              {...register("mobile", {
                required: true,
                maxLength: 10,
                minLength: 10,
              })}
            />
            {errors.mobile && (
              <span className="sp">Mobile No is Not valid </span>
            )}
          </p>
          <select {...register("category")}>
            <option value="">Select...</option>
            <option value="male">MALE </option>
            <option value="female">FEMALE</option>
            <option value="other">OTHER</option>
          </select>
          {errors.exampleRequired && (
            <span className="sp">This field is required</span>
          )}
          <input type="submit" />
        </form>
      </div>
    </>
  );
};

export default RegLog;
