import React, { useEffect, useState } from "react";
import { ClimbingBoxLoader } from "react-spinners";
import { useNavigate } from "react-router-dom";
import { Modal, Button, Group, TextInput, Checkbox } from "@mantine/core";

const ShowTable = () => {
  const navigate = useNavigate();
  const [opened, setOpened] = useState(false);
  const [userData, setuserData] = useState([]);

  const deletItem = (id) => {
    alert(id);

    const data = JSON.parse(localStorage.getItem("register"));
    console.log(555555, data);
    const filtered = data.filter((item, i) => i !== id);
    console.log("filterrrrrrr", filtered);
    localStorage.setItem("register", JSON.stringify(filtered));
    setuserData(filtered);
  };
  const edit = (ele, i) => {
    setOpened(true);
    // const data = {
    //   val: ele,
    //   id: i,
    // };
    // navigate("/register", { state: data });
  };
  useEffect(() => {
    const lenData = JSON.parse(localStorage.getItem("register"));
    console.log(lenData);
    setuserData(lenData);
  }, []);
  return (
    <>
      <div className="tableDiv">
        <h1>ShowTbal</h1>

        <Modal
          opened={opened}
          onClose={() => setOpened(false)}
          title="Introduce yourself!"
        >
          {/* Modal content */}
          <form>
            <TextInput required label="FullNmae" placeholder="your@email.com" />
            <TextInput required label="Email" placeholder="your@email.com" />
            <TextInput required label="Age" placeholder="your@email.com" />
            <TextInput
              required
              label="Mobile No"
              placeholder="your@email.com"
            />
            <TextInput required label="City" placeholder="your@email.com" />

            <Group position="centert" mt="md">
              <Button type="submit">Submit</Button>
            </Group>
          </form>
        </Modal>

        <table className="table table-dark">
          <thead>
            <tr>
              <th scope="col">ID</th>
              <th scope="col">NAME</th>
              <th scope="col">EMAIL</th>
              <th scope="col">AGE</th>
              <th scope="col">MOBILE</th>
              <th scope="col">GENDER</th>
              <th scope="col">CITY</th>
              <th colSpan={2} style={{ textAlign: "center" }}>
                ACTION
              </th>
            </tr>
          </thead>
          <tbody>
            {userData && userData ? (
              userData.map((ele, i) => {
                return (
                  <>
                    <tr>
                      <td>{i + 1}</td>
                      <td>{ele.fullName}</td>
                      <td>{ele.email}</td>
                      <td>{ele.age}</td>
                      <td>{ele.mobile}</td>
                      <td>{ele.gender}</td>
                      <td>{ele.city}</td>
                      <td>
                        <button
                          onClick={() => deletItem(i)}
                          className="btn btn-danger"
                        >
                          DELETE
                        </button>
                      </td>
                      <td>
                        <button
                          className="btn btn-success"
                          onClick={() => edit(ele, i)}
                        >
                          EDIT
                        </button>
                      </td>
                    </tr>
                  </>
                );
              })
            ) : (
              <ClimbingBoxLoader
                cssOverride={{ textAlign: "center", justifyContent: "center" }}
              />
            )}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default ShowTable;
